import React, { Component } from 'react';
import './App.css';
import TaskForm from './components/TaskForm'
import TaskControl from './components/TaskControl'
import TaskList from './components/TaskList'
class App extends React.Component {
  constructor (props){
    super(props);
    this.state={
        tasks: [] //
    }
  }
  onGenered=()=>{
    var tasks=[{
      id: this.generateID(),
      name: "Học React",
      status: true,
    },
    {
      id: this.generateID(),
      name: "Học ReactJS",
      status: false,
    },
    {
      id: this.generateID(),
      name: "Học ReactNative",
      status: true,
    },
  ]
  this.setState({
tasks :tasks
  });
  this.localStorate.setItem('tasks',tasks);
  console.log(tasks);
  }
  randomString(){
    return Math.floor((1+Math.random())*0x1000).toString(16).substring(1);
  }
  generateID(){
    return this.randomString()+this.randomString()+'%'+this.randomString()+'-'+this.randomString()+'$'+'-'+this.randomString();
  }

  render() {
    return (
    <div className="container">
      <div className="text-center">
          <h1>Quản Lý Công Việc</h1><hr/>
      </div>
      <div className="row">
            <div className= '' >
              {/* Form */}
              <TaskForm />
          </div>
          <div className='col-xs-12 col-sm-12 col-md-12 col-lg-12'>
              <button type="button" className="btn btn-primary"  >
                  <span className="fa fa-plus mr-5"></span>
                  Thêm Công Việc
              </button>
              <button type="button" className="btn btn-primary" onClick={this.onGenered}>
                 Generate
              </button>
              {/* Search - Sort */}
              <TaskControl />
              {/* List */}
              <TaskList />
          </div>
      </div>
   </div>
    );
  }
}

export default App;
