import React, { Component } from 'react';
import './App.css';
import TaskForm from './components/TaskForm'
import TaskControl from './components/TaskControl'
import TaskList from './components/TaskList'
class App extends Component {
  constructor (props){
    super(props);
    this.state={
        tasks: [],
        isDisplayForm: false,
    }
  }
  //được gọi khi refresh trang
  componentWillMount(){
    if(localStorage && localStorage.getItem('tasks')){
      var tasks = JSON.parse(localStorage.getItem('tasks'));
      this.setState({
        tasks: tasks
      });
    }

  }
  onGenered=()=>{
    var tasks=[{
      id: this.generateID(),
      name: "Học React",
      status: true,
    },
    {
      id: this.generateID(),
      name: "Học ReactJS",
      status: false,
    },
    {
      id: this.generateID(),
      name: "Học ReactNative",
      status: true,
    },
  ]
  localStorage.setItem('tasks',JSON.stringify(tasks));
  console.log(tasks);
  }
  randomString(){
    return Math.floor((1+Math.random())*0x1000).toString(16).substring(1);
  }
  generateID(){
    return (this.randomString()+this.randomString()+'-%'+this.randomString()
    +'-'+this.randomString()+'$-'+this.randomString());
  }
  onToggetForm=()=>{
    this.setState({
        isDisplayForm : !this.state.isDisplayForm
    });
  }
  onExitForm=()=>{
    this.setState({
      isDisplayForm : false
  });
  }
  onSubmit=(data)=>{
    data.id=this.generateID();
    console.log(data); 

    var {tasks} = this.state;
    tasks.push(data);
    this.setState({
      tasks:tasks
    })

    localStorage.setItem('tasks',JSON.stringify(tasks))
    console.log(tasks);    

  }
  findIndex=(id)=>{
    var {tasks}=this.state;
    var result=-1;
    tasks.forEach((task,index) => {
          if(task.id===id){
            result=index;
          }
    });
    return result;
  }
  onStatusUpdate=(id)=>{
    var {tasks}=this.state;
    var index=this.findIndex(id);
    if(index!==-1){
      tasks[index].status=!tasks[index].status;
    }
       this.setState({
          tasks:tasks
       });
       localStorage.setItem('tasks',JSON.stringify(tasks));
  }

  onDeleteItem=(id)=>{
    var {tasks}=this.state;
    var index=this.findIndex(id);
    if(index!==-1){
      tasks.splice(index,1);
    }
       this.setState({
          tasks:tasks
       });
       localStorage.setItem('tasks',JSON.stringify(tasks));
  }

  render() {
    var {tasks,isDisplayForm}=this.state;//# var tasks = this.state.tasks
    var elmTaskForm = isDisplayForm ? <TaskForm 
    onExitForm={this.onExitForm}
    onSubmit={this.onSubmit}/>:'';
    return (
    <div className="container">
      <div className="text-center">
          <h1>Quản Lý Công Việc</h1><hr/>
      </div>
      <div className="row">
            <div className= {isDisplayForm?'col-xs-4 col-sm-4 col-md-4 col-lg-4':''} >
              {/* Form */}
             {elmTaskForm}
          </div>
          <div className={isDisplayForm?'col-xs-8 col-sm-8 col-md-8 col-lg-8':'col-xs-12 col-sm-12 col-md-12 col-lg-12'}>
              <button type="button" className="btn btn-primary"  onClick={this.onToggetForm}>
                  <span className="fa fa-plus mr-5"></span>
                  Thêm Công Việc
              </button> &nbsp;
              <button type="button" className="btn btn-danger" onClick={this.onGenered}>
                 Generate
              </button>
              {/* Search - Sort */}
              <TaskControl />
              {/* List */}
              <TaskList tasks={tasks} 
              onStatusUpdate={this.onStatusUpdate}
              onDeleteItem={this.onDeleteItem}/>
          </div>
      </div>
   </div>
    );
  }
}

export default App;
